graphql.query:
query AllLifts($statusLift1: LiftStatus $statusLift2: LiftStatus $statusLift3: LiftStatus) {
    HoldLifts: allLifts(status: $statusLift1) {
        id
        name
        status
    }
    OpenLifts: allLifts(status: $statusLift2) {
        id
        name
        status
    }
    ClosedLifts: allLifts(status: $statusLift3) {
        id
        name
        status
    }
}
